// Combine all "text" fields from the content array into one string
let script = '';

// Loop through all input items
for (const item of items) {
  if (item.json.content && Array.isArray(item.json.content)) {
    for (const segment of item.json.content) {
      if (segment.text) {
        script += segment.text + ' ';
      }
    }
  }
}

script = script.trim();

return [
  {
    json: {
      script
    }
  }
];

