// Extract video URLs, titles, and add an id starting from 2
return items.map((item, index) => {
  const videoId = item.json.snippet.resourceId.videoId;
  const title = item.json.snippet.title;
  return {
    json: {
      id: index + 2,
      url: `https://www.youtube.com/watch?v=${videoId}`,
      title: title
    }
  };
});

