return items.map((item, index) => {
  // Get the URL and title from the input
  const url = item.json.url;
  const title = item.json.title || null;
  // Extract the video ID from the URL
  let videoId = null;
  if (url && url.includes('v=')) {
    videoId = url.split('v=')[1].split('&')[0];
  }
  // id starts from 1; change to index+2 if you want to start from 2
  return {
    json: {
      id: index + 2,
      videoId,
      title
    }
  };
});

// Get the script text
const script = items[0].json.script || "";

// Split into sentences using a regular expression
const sentences = script.match(/[^\.!\?]+[\.!\?]+/g) || [];

// Get the first 10 sentences (or fewer if less than 10)
const firstTen = sentences.slice(0, 5).join(' ').trim();

return [
  {
    json: {
      firstTenSentences: firstTen
    }
  }
];
